INSERT INTO `cookbook` VALUES ('97c0edb6-2768-4391-bf00-2d555ba22e8f', '2015-08-15 16:50:39', null, '【2店通用】重庆豆花石锅鱼', '0', null, '0', null, null, null, '/uploadfile/2015-08-15/a261541f304646f48ef58f80498acc1c.jpg', null, 'f934ad79-fc38-4fdb-88a0-76b922249b4b', null, '89.8', '<span style=\"color:#666666;font-family:\'Hiragino Sans GB\', \'WenQuanYi Micro Hei\', tahoma, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">庆豆花石锅鱼，石和鱼而烹，顾名思义，就是说石和鱼结合的一种美食。石锅鱼顺应了餐饮业向滋补养生发展趋势，在人类正经历严重的亚健康时代，对从饮食方面克服亚健康现象开辟了一条途径。石锅鱼以鱼、虾等主打美食产品，经过独特而复杂的烹调流程后，放入切好的生鱼片，边烫边吃，在确保原汁原味的同时，又加入独家秘制的佐料，油和高汤，使营养物质渗透到鱼中，更使美食产品中的各种营养成分被人体吸收利用，有益健康</span>', null, '0', null, '水产', null, null);
