INSERT INTO `cookbook` VALUES ('f4bba21a-770a-4543-93aa-184d931542fe', '2015-08-13 00:09:24', null, '猪肚', '0', null, '0', null, null, null, '/uploadfile/2015-08-13/f4355142f4b54460bd5026eec2791d09.jpg', null, 'f8c0a4be-92e9-4ff0-81fa-c8cc29d6db74', null, '30', '<span style=\"color:#666666;font-family:\'Hiragino Sans GB\', \'WenQuanYi Micro Hei\', tahoma, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">李记猪肚鸡是南宁非常出名的食馆，李记的猪肚鸡不但味美鲜甜，而且开胃营养。李记猪肚鸡在南宁拥有多家分店，凭着过硬的美食功夫和良好的市民口碑，早就打开了猪肚鸡新局面，深受市民欢迎。喜欢猪肚鸡的朋友们千万不要错过哦。猪肚鸡是客家地区酒席必备的餐前用汤，又名凤凰投胎，汤里浓中带清，有浓郁的药材味和胡椒香气。吃法也很有讲究：首先饮原汁原味的猪肚包鸡汤；接着将猪肚包鸡放入汤中继续煲5-10分钟，吃猪肚和鸡肉，猪肚爽口异常，鸡肉鲜嫩可口，汤的味道更增添了鸡的鲜味；然后放入菜干，香菇等干类杂菜，吸收肉味，汤味变得清甜；最后加入肉丸，鲜鸡什，竹肠等肉类，汤水越加浓郁美味。</span>', null, '0', null, '其它', null, null);
