INSERT INTO `shop` VALUES ('1ee764b9-66c5-4935-aa86-293fa60ff57f', '2015-08-13 07:53:11', '西乡塘区科园大道科德路口（科德路中段，翰林雅筑对面）', null, '17.8', null, null, 'WIFI', '1', null, '0', '22.853189', '【高新区】樽记颐品斋素菜', '/uploadfile/2015-08-13/d2e3137037dc4608b24a5c8aa2793558.jpg', '108.277309', '0', 'bb788ead-324b-439d-a55b-5fac6dcc8720', 'b145e065-eafe-4228-97b5-ed41bf6bc8f3', '8:00~23:00', '0', '<span style=\"color:#666666;font-family:\'Hiragino Sans GB\', \'WenQuanYi Micro Hei\', tahoma, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">樽记颐品斋是一家新兴的健康时尚素食餐厅，提供自助餐及包厢点餐两种服务模式。颐品斋餐厅面积超过1000平方米，设计温馨舒适高雅。“自然、健康、时尚”是颐品斋经营的出发点，对待食材，诚如五谷果蔬，我们希望它们沐浴阳光、经历风雨，逐渐成长，在最适合它的生长环境，经过不多不少的生长周期，凝聚的自然精华，焕发天然的味道；对于烹饪技艺，我们追求用最健康的方式来体现，不破坏食材的营养而又美味可口，给顾客带来就餐的愉悦。让我们从自然中来的产品，带着自然美好的气息，经过我们富含爱心的厨师的手，变成一道道营养、健康、美味菜肴，回馈爱护自然家园的人们。</span>', '【高新区】【高新区】樽斋素菜馆', '0771-3866999', '自助餐', null, null, null, null);
