INSERT INTO `shop` VALUES ('30fb1fb6-642c-4750-a26f-b9216e5d81f0', '2015-08-12 22:50:31', '西乡塘区衡阳西路14号', null, '50', null, null, 'WIFI,停车场', '0', null, '0', '0', '肥仔', '/uploadfile/2015-08-12/8a0a3a06ab9b46a6bf5821bc27cc98c3.jpg', '0', '0', 'bb788ead-324b-439d-a55b-5fac6dcc8720', '400baa01-ab63-48d9-bf28-9ba8b93e9b83', '8:00~00:00', '0', '<span style=\"color:#666666;font-family:\'Hiragino Sans GB\', \'WenQuanYi Micro Hei\', tahoma, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">南宁肥仔海鲜美食广场是由南宁肥仔餐饮有限责任公司投资兴建的一家大型酒楼，位于南宁市衡阳西路14号（衡阳小学旁），拥有60多间风格各异的包厢，多个中型宴会包厢及多功能包厢，二个豪华宴会大厅，可同时容纳1600多人就餐，经营中、晚餐，承接各类大中型社团宴会及中西式婚宴，作为以南宁为基础、辐射广西的南宁肥仔餐饮企业，携屡获殊荣的厨师团队和热情好客的服务团队，诚挚欢迎所有宾客光临。 创店伊始，滕锡联董事长本着“顾客就是亲人，以宾客为皇帝，效益为命，制度为本”的经营理念和“诚实做人，认真做事”的信念，勇于探索，锐意创新。根据南宁市地处桂东南的气候条件以及人文习惯，以传统粤菜为基础，结合中华传统美食各大菜系的特点，依据以味为核心，以营养为目的，适口为珍的餐饮要求，烹制过程精细加工，讲究火候，追求“天然、绿色、健康。以“咸而适中、辣而适口、甜而有味、鲜香嫩爽”独树一帜的肥仔风味菜系，赢得了广大顾客的称誉。 在经营中，逐步完善了符合现代餐饮企业经营管理的运营机制，重视品牌效益，并于2001年在国家商标局注册了“肥仔”商标。如今，公司已发展为广西各城市9家连锁饭店和加拿大多伦多饭店的大型餐饮企业。每年上缴国家税利200多万。公司在各类评选和口碑中获得一致的美誉</span>', '【衡阳路】南宁肥仔海鲜美食广场', '0771-3129668', '海鲜', null, null, null, null);
