INSERT INTO `shop` VALUES ('638fe1d9-3e0a-4dcc-911e-3527d9111df2', '2015-08-13 08:54:00', '西乡塘区江北大道广西经济管理干部学院旁（醉金陵旁）', null, '88', null, null, 'WIFI,停车场', '1', null, '0', '22.837286', '品佳', '/uploadfile/2015-08-13/271a6e4889344590b358937f65949776.jpg', '108.245661', '0', 'bb788ead-324b-439d-a55b-5fac6dcc8720', '366d3eef-d284-46c3-b59b-822a94bfe5f2', '8:00~00:00', '0', '<span style=\"color:#666666;font-family:\'Hiragino Sans GB\', \'WenQuanYi Micro Hei\', tahoma, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">品佳金记猪肚鸡是南宁非常出名的食馆，金记的猪肚鸡不但味美鲜甜，而且开胃营养。品佳金记在南宁拥有多家分店，凭着过硬的美食功夫和良好的市民口碑，早就打开了猪肚鸡新局面，深受市民欢迎。喜欢猪肚鸡的朋友们千万不要错过哦。猪肚鸡是客家地区酒席必备的餐前用汤，又名凤凰投胎，汤里浓中带清，有浓郁的药材味和胡椒香气。吃法也很有讲究：首先饮原汁原味的猪肚包鸡汤；接着将猪肚包鸡放入汤中继续煲5-10分钟，吃猪肚和鸡肉，猪肚爽口异常，鸡肉鲜嫩可口，汤的味道更增添了鸡的鲜味；然后放入菜干，香菇等干类杂菜，吸收肉味，汤味变得清甜；最后加入肉丸，鲜鸡什，竹肠等肉类，汤水越加浓郁美味</span>', '【相思湖】品佳金记猪肚鸡', '0771-3247773', '特色菜', null, null, null, null);
