INSERT INTO `user_info` VALUES ('70242786-e766-49e5-8ae2-ac09df1b5c77', '2015-08-14 23:38:28', '1', 'jinggong', '34a0990a3b39cdc0a0b39fbfc4c56ff9', '/uploadfile/2015-08-14/6e62d8f299ff41399d4ad2c0bc50bd68.jpg', '										<span style=\"color:#666666;font-family:\'Hiragino Sans GB\', \'WenQuanYi Micro Hei\', tahoma, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\">精功眼镜自1989年成立以来，经过二十三年的发展壮大，已成为一家集验光、配镜、生产、销售及代理为一体的大型连锁企业。全体精功人本着以独到功夫打造眼镜精品，全心全意为顾客的眼睛健康服务的理念，不断开拓创新。公司发展至今，已逐步形成自己独特的品牌优势：精品荟萃、质优价廉、人员专业、管理规范、设备先进、环境舒适。精功眼镜一直秉承“诚信为本、服务至上、专业连锁、期待共赢”的企业连锁宗旨，坚定不移地以连锁提升品牌实力和推进企业扩张，扩大影响力与辐射力。截至目前，精功眼镜现已遍布全国29个省市，销售业绩稳健骄人，在市场上享有很高的知名度和美誉度。香港、北京、浙江、四川、上海分公司构建起精功眼镜完善而庞大的全国销售与服务网络，为全国各连锁店和广大消费者提供方便、快捷和完善的服务。</span>\r\n								', '0771-2613667', '2015-08-14 23:39:46', '精功眼镜', '1', '48bb9d31-8671-4f21-a07e-87d34125f370');
