INSERT INTO `user_info` VALUES ('faba23ed-3e4d-454f-a951-794818f1fc60', '2015-08-13 09:09:38', '1', 'yasitenanhu', '3e1ee3665d56421553c7f1512184f0b1', '/uploadfile/2015-08-13/cdd4bb29124442409af6a746fe969a40.jpg', '<span style=\"color:#666666;font-family:\'Hiragino Sans GB\', \'WenQuanYi Micro Hei\', tahoma, sans-serif;font-size:12px;line-height:20px;background-color:#FFFFFF;\">南宁雅斯特酒店（南湖公园店）青秀区双拥路26-96号（广西医科大学正门右侧）。 酒店采用地中海风情的设计元素，精致、简约、时尚的装饰风格，精心打造一个“雅致、静美、绿色、创意”的居住空间。在雅斯特酒店，您拥有的不仅仅是舒适的大床、洁净的环境、高速互联网、现代淋浴空间、美味营养的自助早餐以及甜美、友好的微笑，您获得的是一次无与伦比的轻松住宿体验。 从您走进酒店的那一刻起，自助开房的功能让您无需在总台多等待一分钟，即可选择您喜欢的客房自由操作入住；酒店的“一卡通”让您只需轻松刷卡，开房、乘电梯、开房门、取电等功能瞬间实现；酒店大堂的咖啡、果汁，商务电脑您都可通过刷卡免费享用！设施完善的自助洗衣房、格调高雅的休闲书吧，伴您度过一段难忘的商旅时光，开一支红酒，冲一杯香浓的咖啡，享受一下小资生活，客房的冰柜里已经为您准备好了一切。这就是雅斯特酒店诠释的酒店人生：努力工作，尽情享受生活，年轻进取的心态、一个充满活力的群体！湛蓝的大海，迷人的地中海风情，一个安逸的自由港。雅斯特精品酒店系“亚洲最具魅力酒店”、“2014年中国酒店金马奖百佳酒店”、 “2012年度最佳消费者满意酒店”、中国饭店名人俱乐部（CHICC）企业品牌。</span>', '0771-6110188', null, '雅斯特酒店（南湖公园店）', '1', '48bb9d31-8671-4f21-a07e-87d34125f370');
